import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import UserContext from '../../context/UserContext';
import clsx from 'clsx';
import { makeStyles, AppBar, Toolbar, IconButton, Typography, 
  SwipeableDrawer, List, ListItem, Divider, ListItemIcon, ListItemText
} from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';
import AccountCircle from '@material-ui/icons/AccountCircle';
import InboxIcon from '@material-ui/icons/MoveToInbox';
// import Drawer from './Drawer';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    width: '100%',
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  list: {
    width: 250,
  },
  leftMargin: {
    marginLeft: '18px',
  },
  linkDecoration: {
    textDecoration: 'inherit',
    color: 'inherit',
  }
}));

const Header = () => {
  const classes = useStyles();
  const { userData, setUserData } = useContext(UserContext);
  const [drawerState, setDrawerState] = useState({
    left: false,
    right: false
  });
  console.log('=== Header ===');
  console.log(userData);

  const toggleDrawer = (anchor, open) => (event) => {
    if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setDrawerState({ ...drawerState, [anchor]: open });
  };

  const leftDrawerList = (anchor) => (
    <div
      className={clsx(classes.list)}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
        <Typography variant="h6" className={classes.leftMargin}>
          Menu
        </Typography>
        <Link to="/catalog" className={classes.linkDecoration}>
          <ListItem button>
            <ListItemIcon>
              <InboxIcon />
            </ListItemIcon>
            <ListItemText 
              primary='Catalog' 
              // secondary='secondary'
            />
          </ListItem>
        </Link>
        <ListItem button>
          <ListItemIcon>
            <InboxIcon />
          </ListItemIcon>
          <ListItemText primary='Sales platform' />
        </ListItem>
      </List>
      <Divider />
    </div>
  );

  const rightDrawerList = (anchor) => (
    <div
      className={clsx(classes.list)}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
        <Typography variant="h6" className={classes.leftMargin}>
          Username
        </Typography>
        <ListItem button>
          <ListItemIcon>
            <InboxIcon />
          </ListItemIcon>
          <ListItemText 
            primary='Catalog' 
            // secondary='secondary'
          />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <InboxIcon />
          </ListItemIcon>
          <ListItemText primary='Sales platform' />
        </ListItem>
      </List>
      <Divider />
    </div>
  );

  // if(!userData.token){
  //   return <div />
  // }

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <IconButton onClick={toggleDrawer('left', true)} edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
              <MenuIcon/>
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            My store catalog
          </Typography>
          <IconButton onClick={toggleDrawer('right', true)}  color="inherit">
            <AccountCircle />
          </IconButton>
        </Toolbar>
      </AppBar>
      <SwipeableDrawer
        anchor='left'
        open={drawerState['left']}
        onClose={toggleDrawer('left', false)}
        onOpen={toggleDrawer('left', true)}
      >
        {leftDrawerList('left')}
      </SwipeableDrawer>
      <SwipeableDrawer
        anchor='right'
        open={drawerState['right']}
        onClose={toggleDrawer('right', false)}
        onOpen={toggleDrawer('right', true)}
      >
        {rightDrawerList('right')}
      </SwipeableDrawer>
    </div>
  )
}

export default Header;

