import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { makeStyles, Card, CardHeader, CardContent, Menu, MenuItem  } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  cardRoot: {
    margin: '10px',
    maxHeight: '737px',
    boxShadow: '0px 0px 3px 0px rgba(0,0,0,0.6)',
  },
  cardContentRoot: {
    overflowY: 'scroll',
    maxHeight:'662px',
    paddingTop: '0',
  },
  linkDecoration: {
    textDecoration: 'inherit',
    color: 'inherit',
  },
}));

const CatalogItemDetail = ({display, selectedItem}) => {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = useState(null);
  // const open = Boolean(anchorEl);

  const handleMoreIconClicked = (event) => {
    setAnchorEl(event.currentTarget);
  }

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleDelete = () => {
    setAnchorEl(null);
    axios({
      method:'delete',
      url: process.env.REACT_APP_API+'/api/inventory',
      data: selectedItem
    }).then(function(deletedInventory){
      console.log(deletedInventory);
    }).catch(function(err){
      console.log(err);
    })
  }

  if(!selectedItem || display){
    return <div />
  }

  return (
    <div className={classes.root}>
      <Card className={classes.cardRoot}>
        <CardHeader
          title="Item detail"
          action={
            <>
              <IconButton aria-label="settings" onClick={handleMoreIconClicked}>
                <MoreVertIcon />
              </IconButton>
              <Menu
                id="simple-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
              >
                <Link 
                  to={{
                    pathname:"/catalog/form",
                    selectedItem: selectedItem,
                    isEdit: true
                  }} 
                  className={classes.linkDecoration}
                >
                  <MenuItem onClick={handleClose}>
                    Edit
                  </MenuItem>
                </Link>
                <MenuItem onClick={handleDelete}>Delete</MenuItem>
              </Menu>
            </>
          }
        />
        <CardContent className={classes.cardContentRoot}>
          {selectedItem._id} <br /> <br />
          {selectedItem.partNumber} <br /> <br />
          {selectedItem.description} <br /> <br />
          {selectedItem.brand} <br /> <br />
          {selectedItem.category} <br /> <br />
        </CardContent>
      </Card>
    </div>
  )
}

export default CatalogItemDetail
