import React, { useState } from 'react';
import clsx from 'clsx';
import { makeStyles, AppBar, Toolbar, IconButton, Typography, Button,
  SwipeableDrawer, List, Divider
} from '@material-ui/core';
import StoreIcon from '@material-ui/icons/Store';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import LinkButton from './LinkButton';

const useStyles = makeStyles((theme) => ({
  list: {
    width: 250,
  },
  leftMargin: {
    marginLeft: '18px',
  },
}));

const LeftDrawer = () => {
  const classes = useStyles();
  const [drawerState, setDrawerState] = useState(false);

  const toggleDrawer = (open) => (event) => {
    if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setDrawerState({ ...drawerState, open });
  };

  const leftDrawerList = (anchor) => (
    <div
      className={clsx(classes.list)}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
        <Typography variant="h6" className={classes.leftMargin}>
          Menu
        </Typography>
        <LinkButton 
          LinkTitle='Catalog'
          LinkUrl='/catalog'
          LinkIcon={<StoreIcon />}
        />
        <LinkButton 
          LinkTitle='Sales platform'
          LinkUrl='/'
          LinkIcon={<MonetizationOnIcon />}
        />
      </List>
      <Divider />
    </div>
  );

  return (
    <>
      <SwipeableDrawer
        anchor='left'
        open={drawerState}
        onClose={toggleDrawer('left', false)}
        onOpen={toggleDrawer('left', true)}
      >
        {leftDrawerList('left')}
      </SwipeableDrawer>
    </>
  )
}

export default LeftDrawer;
