import React from 'react';
import { makeStyles } from '@material-ui/core';
import PageTitle from '../../Components/Layout/PageTitle';
import ListCard from '../../Components/Layout/ListCard';

import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';

const useStyles = makeStyles((theme)=> ({
  root: {
    height: '100%',
  },
  listRoot: {
    overflowY: 'scroll',
    height: 'calc(100% - 62px - 50px)',
    width: '100%',
  },
  fab: {
    position: 'absolute',
    bottom: theme.spacing(2),
    left: '90%',
    // bottom: theme.spacing(2),
    // left: theme.spacing(55),
    // right: theme.spacing(2),
  },
}));

const CatalogList = ({catalogItems, handleSelectitemChange}) => {
  const classes = useStyles();

  const displayList = catalogItems.map((value, key) => {
    return (
      <ListCard
        key={key}
        id={value._id}
        title={value.partNumber}
        subTitle={value.description}
        handleSelectitemChange={() => handleSelectitemChange(value)}
        value={value}
      />
    )
  });

  return (
    <div className={classes.root}>
      <PageTitle title="Catalog" variant="h4" />
      <div className={classes.listRoot}>
        {displayList}
      </div>
      <Fab color="primary" className={classes.fab}>
        <AddIcon 
          onClick={() => {
            console.log('FAB Clicked');
          }} 
        />
      </Fab>
    </div>
  )
}

export default CatalogList;
