import React from 'react';
import PropTypes from 'prop-types';
import { Route, Redirect } from 'react-router-dom';
import axios from 'axios';

const RouteWrapper = async ({
  component: Component,
  isPrivate,
  userData,
  ...rest
}) => {
  // const signed = userData.token;
  // const signed = localStorage.getItem('auth-token')
  // Route is private and the user is not logged in

  let token = localStorage.getItem('auth-token');
  if(token === null) {
    localStorage.setItem('auth-token','');
    token='';
  }

  const tokenRes = await axios.post(
    'http://localhost:3030/api/auth/tokenIsValid',
    null,
    {headers: {'auth-token': token}}
  )
    console.log(tokenRes)
  if (isPrivate && !tokenRes.data) {
    return <Redirect to="/login" />;
  }

  return (
    <Route {...rest} component={Component} />
  )
}

export default RouteWrapper;

RouteWrapper.propTypes = {
  isPrivate: PropTypes.bool,
  component: PropTypes.oneOfType([PropTypes.element, PropTypes.func]).isRequired
}

RouteWrapper.defaultProps = {
  isPrivate: false
}
