import React, { useState } from 'react';
import { makeStyles, Typography, Card, CardHeader, 
  // IconButton
} from '@material-ui/core';
// import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme)=> ({
  cardStyle: {
    margin: '10px',
    boxShadow: '0px 0px 3px 0px rgba(0,0,0,0.6)',
    // backgroundColor: 'red',
  },
  cardHeaderStyle: {
    cursor: 'pointer',
  },
  cardContentRoot: {
    paddingTop: '0px',
  },
}));

const ListCard = ({
  value,
  id,
  title,
  subTitle,
  contentTitle,
  content,
  handleSelectitemChange,
  selectedItem
}) => {
  const classes = useStyles();
  const [selectedId, setSelectedId] = useState(selectedItem);
  let boxShadowStyle = '0px 0px 3px 0px rgba(0,0,0,0.6)';

  if (title.length !== 1) {
    title = title.join(', ');
  }

  if (id === selectedId._id) {
    boxShadowStyle = '0px 0px 5px 0px #3f51b5';
    console.log('====')
  }

  return (
    <Card className={classes.cardStyle} style={{ boxShadow: {boxShadowStyle} }}>
      <CardHeader
        title={
          <Typography 
            className={classes.cardHeaderStyle}
            variant='h6'
            onClick={() => handleSelectitemChange(value)}
          >
            {title}
          </Typography>
        }
        subheader={subTitle}
        // action={
        //   <IconButton aria-label="settings">
        //     <ExpandMoreIcon />
        //   </IconButton>
        // }
      />
      {/* <CardContent className={classes.cardContentRoot}>
        <Typography variant="body2" color="textSecondary" component="p">
          {contentTitle}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          {content}
        </Typography>
      </CardContent> */}
    </Card>
  )
}

export default ListCard;
