import React from 'react';
import { makeStyles, Typography, Card, CardHeader, CardContent, IconButton } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme)=> ({
  cardStyle: {
    margin: '10px',
    boxShadow: '0px 0px 3px 0px rgba(0,0,0,0.6)',
    // backgroundColor: 'red',
  },
  cardHeaderStyle: {
    cursor: 'pointer',
  },
  cardContentRoot: {
    paddingTop: '0px',
  },
}));

const ListCard = ({title, subTitle, contentTitle, content}) => {
  const classes = useStyles();

  const handleSelect = (e) => {
    // console.log(e)
    console.log('Selected');
  }

  return (
    <Card className={classes.cardStyle}>
      <CardHeader
        title={
          <Typography variant='h6' onClick={handleSelect} className={classes.cardHeaderStyle}>
            {title}
          </Typography>
        }
        subheader={subTitle}
        action={
          <IconButton aria-label="settings">
            <ExpandMoreIcon />
          </IconButton>
        }
      />
      <CardContent className={classes.cardContentRoot}>
        <Typography variant="body2" color="textSecondary" component="p">
          {contentTitle}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          {content}
        </Typography>
      </CardContent>
    </Card>
  )
}

export default ListCard;
