import React, { useState, useRef } from 'react';
import { FormControl, TextField, Typography, makeStyles, Button, Chip } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  formControl: {
    // margin: theme.spacing(0.5),
    width: '100%',
  },
  addButtonStyle: {
    margin: '10px 0px 0px 0px'
  },
  chipStyle: {
    margin: '5px 0px 0px 0px',
    width: '24%',
  }
}));

const FormTextMulti = (props) => {
  console.log('=== FormTextMulti ===');
  const classes = useStyles();
  const [fieldValue, setFieldValue] = useState(props.field.value);
  // const chipRef = useRef(null);
  const chipRef = fieldValue.map(value => useRef(null));
  console.log(chipRef);

  const addItem = () => {
    console.log('Clicked');
  }

  const handleDelete = (e) => {
    console.log('=== delete ===');
    console.log(chipRef.current.children[0].textContent);
    chipRef.current.remove();
  }

  const displayFieldValue = fieldValue.map((value, key) => {
    console.log(value)
    console.log(key)
    
    return (
      <Chip 
        key={key}
        label={value}
        onDelete={handleDelete}
        color="primary"
        className={classes.chipStyle}
        ref={chipRef}
      />
    )
  });

  // const displayFieldValue = fieldValue.map((value, key) => (
  //   <Chip 
  //     key={key}
  //     label={value}
  //     onDelete={handleDelete}
  //     color="primary"
  //     className={classes.chipStyle}
  //     ref={chipRef}
  //   />
  // ));

  return (
    <div style={{ marginTop: 10 }}>
      <FormControl className={classes.formControl}>
        <Typography variant="subtitle2" display="block">
          {props.title}
        </Typography>
        <TextField
          name={props.field.name}
          type={props.type}
          disabled={props.disabled}
          // defaultValue={props.field.value}
          // onChange={props.form.handleChange}
          variant="standard"
          size="medium"
        />
        <Button variant="contained" color="primary" onClick={addItem} className={classes.addButtonStyle}>
          Add
        </Button>
        <div>
          {displayFieldValue}
        </div>
      </FormControl>
    </div>
  )
}

export default FormTextMulti;
