import React from 'react';
import { makeStyles, Typography, Card, CardHeader, CardContent, Grid } from '@material-ui/core';
import CatalogOptions from './CatalogOptions';

const useStyles = makeStyles((theme) => ({
  cardRoot: {
    margin: '10px',
    maxHeight: '737px',
    boxShadow: '0px 0px 3px 0px rgba(0,0,0,0.6)',
  },
  cardContentRoot: {
    overflowY: 'scroll',
    maxHeight:'662px',
    paddingTop: '0',
  },
}));

const CatalogItemDetail = ({display, selectedItem}) => {
  const classes = useStyles();

  if(!selectedItem || display){
    return <div />
  }

  return (
    <div className={classes.root}>
      <Card className={classes.cardRoot}>
        <CardHeader
          title="Item detail"
          action={
            <CatalogOptions selectedItem={selectedItem} />
          }
        />
        <CardContent className={classes.cardContentRoot}>
          <Grid container spacing={1}>
            <Grid item xs={6} sm={2}>
              <Typography variant='subtitle2'>
                Brand:
              </Typography>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Typography variant='subtitle2'>
                {selectedItem.brand}
              </Typography>
            </Grid>
            <Grid item xs={6} sm={2} />
            <Grid item xs={6} sm={2}>
              <Typography variant='subtitle2'>
                Brand:
              </Typography>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Typography variant='subtitle2'>
                {selectedItem.brand}
              </Typography>
            </Grid>
          </Grid>

          {selectedItem.partNumber} <br /> <br />
          {selectedItem.description} <br /> <br />
          {selectedItem.brand} <br /> <br />
          {selectedItem.category} <br /> <br />
        </CardContent>
      </Card>
    </div>
  )
}

export default CatalogItemDetail
