import React, { useEffect, useState } from 'react';
import { makeStyles, Typography,
  ExpansionPanel, ExpansionPanelSummary, ExpansionPanelDetails,
  Table, TableBody, TableRow, TableCell
} from '@material-ui/core';
import axios from 'axios';
import PageTitle from '../../Components/Layout/PageTitle';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
  expansionPanalRoot: {
    margin: '0px 10px 0px 10px',
    boxShadow: '0px 0px 3px 0px rgba(0,0,0,0.6)',
  },
  fab: {
    position: 'absolute',
    bottom: theme.spacing(2),
    left: theme.spacing(2),
  },
}));

const CatalogMinimise = ({showRedirect}) => {
  const classes = useStyles();
  const [catalogItems, setCatalogItems] = useState([]);

  useEffect(() => {
    axios({
      method:'get',
      url: process.env.REACT_APP_API+'/api/inventory'
    }).then(function(inventory){
      setCatalogItems(inventory.data);
    }).catch(function(err){
      console.log(err);
    })
  }, []);

  const displayList = catalogItems.map((value, key) => {
    let title = value.partNumber;
    if (title.length !== 1) {
      title = title.join(', ');
    }
    const displayPartNumber = value.partNumber.map(value => {
      return (
        <Typography>{value}</Typography>
      )
    })
    return (
      <ExpansionPanel className={classes.expansionPanalRoot} key={key}>
        <ExpansionPanelSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography className={classes.heading}>
            {title}
          </Typography>
        </ExpansionPanelSummary>
        <ExpansionPanelDetails>
          <Table size="small">
            <TableRow>
              <TableCell>Category</TableCell>
              <TableCell>
                <Typography>{value.category}</Typography>
              </TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Brand</TableCell>
              <TableCell>
                <Typography>{value.brand}</Typography>
              </TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Part number</TableCell>
              <TableCell>
                {displayPartNumber}
              </TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Description</TableCell>
              <TableCell>
                <Typography>{value.description}</Typography>
              </TableCell>
            </TableRow>
          </Table>
        </ExpansionPanelDetails>
      </ExpansionPanel>
    )
  });

  return (
    <div className={classes.root}>
      <PageTitle title="Catalog" variant="h4" />
      {displayList}

      <Fab color="primary" className={classes.fab}>
        <AddIcon 
          // onClick={() => {
          //   history.push({
          //     pathname: '/catalog/form',
          //     selectedItem: selectedItem,
          //     isEdit: false
          //   });
          // }}
        />
      </Fab>
    </div>
  )
}

export default CatalogMinimise;
