import React, { useState } from 'react';
import { FormControl, TextField, Typography, makeStyles, Button, Chip } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  formControl: {
    // margin: theme.spacing(0.5),
    width: '100%',
  },
  addButtonStyle: {
    margin: '10px 0px 0px 0px'
  },
  chipStyle: {
    margin: '3px 0px 0px 0px',
    width: '24%',
  }
}));

const FormTextMulti = (props) => {
  console.log('=== FormTextMulti ===');
  const classes = useStyles();
  const [fieldValue, setFieldValue] = useState(props.field.value)  
  console.log(fieldValue);

  const addItem = () => {
    console.log('Clicked');
  }

  const handleDelete = () => {
    console.log('delete');
  }

  const displayFieldValue = fieldValue.map((value) => (
    <Chip label={value} onDelete={handleDelete} color="primary" className={classes.chipStyle} />
  ));

  return (
    <div style={{ marginTop: 10 }}>
      <FormControl className={classes.formControl}>
        <Typography variant="subtitle2" display="block">
          {props.title}
        </Typography>
        <TextField
          name={props.field.name}
          type={props.type}
          disabled={props.disabled}
          // defaultValue={props.field.value}
          // onChange={props.form.handleChange}
          variant="standard"
          size="medium"
        />
        <Button variant="contained" color="primary" onClick={addItem} className={classes.addButtonStyle}>
          Add
        </Button>
        <div>
          {displayFieldValue}
        </div>
      </FormControl>
    </div>
  )
}

export default FormTextMulti;
