import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core'; 
import axios from 'axios';
import CatalogList from './CatalogList';
import CatalogItemDetail from './CatalogItemDetail';
import CatalogForm from './CatalogForm';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';

const useStyles = makeStyles((theme)=> ({
  root: {
    display: 'flex',
  },
  leftPanel: {
    width: '35vw',
    minWidth: '350px',
    height: 'calc(100vh - 60px)',
    borderRight: '1px solid #C0C0C0',
    backgroundColor: '#F5F5F5',
  },
  rightPanel: {
    width: '65vw',
    height: 'calc(100vh - 60px)',
    backgroundColor: '#F5F5F5',
  },
  fab: {
    position: 'absolute',
    bottom: theme.spacing(2),
    left: '30%',
  },
}));

const CatalogMaximise = () => {
  const classes = useStyles();
  const [catalogItems, setCatalogItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [displayForm, setDisplayForm] = useState(false);

  useEffect(() => {
    axios({
      method:'get',
      url: process.env.REACT_APP_API+'/api/inventory'
    }).then(function(inventory){
      setCatalogItems(inventory.data);
    }).catch(function(err){
      console.log(err);
    })
  }, []);

  const handleSelectitemChange = (item) => {
    setDisplayForm(false);
    setSelectedItem(item);
  }

  return (
    <div className={classes.root}>
      <div className={classes.leftPanel}>
        <CatalogList
          catalogItems={catalogItems} 
          handleSelectitemChange={(item) => handleSelectitemChange(item)} 
        />
      </div>
      <div className={classes.rightPanel}>
        <CatalogItemDetail
          display={displayForm}
          selectedItem={selectedItem}
        />
        <CatalogForm 
          display={displayForm}
          electedItem={selectedItem}
        />
      </div>
      <Fab color="primary" className={classes.fab}>
        <AddIcon 
          onClick={() => {
            setDisplayForm(true);
          }} 
        />
      </Fab>
    </div>
  )
}

export default CatalogMaximise;
