import React, { useState } from 'react';
import { FormControl, TextField, Typography, makeStyles, Button, Chip } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  formControl: {
    // margin: theme.spacing(0.5),
    width: '100%',
  },
  chipStyle: {
    margin: '10px',
    width: '30%',
  }
}));

const FormTextMulti = (props) => {
  console.log('=== FormTextMulti ===');
  console.log(props);
  const classes = useStyles();

  const addItem = () => {
    console.log('Clicked');
  }

  const handleDelete = () => {
    console.log('delete');
  }

  return (
    <div style={{ marginTop: 10 }}>
      <FormControl className={classes.formControl}>
        <Typography variant="subtitle2" display="block">
          {props.title}
        </Typography>
        <TextField
          name={props.field.name}
          type={props.type}
          disabled={props.disabled}
          defaultValue={props.field.value}
          // onChange={props.form.handleChange}
          variant="standard"
          size="medium"
        />
        <Button variant="contained" color="primary" onClick={addItem}>
          Add
        </Button>
        <Chip label="Deletable primary" onDelete={handleDelete} color="primary" className={classes.chipStyle} />
      </FormControl>
    </div>
  )
}

export default FormTextMulti;
