import React from 'react';
import { makeStyles, Card, CardHeader, CardContent  } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import MoreVertIcon from '@material-ui/icons/MoreVert';

const useStyles = makeStyles((theme) => ({
  cardRoot: {
    margin: '10px',
    maxHeight: '737px',
    boxShadow: '0px 0px 3px 0px rgba(0,0,0,0.6)',
  },
  cardContentRoot: {
    overflowY: 'scroll',
    maxHeight:'662px',
    paddingTop: '0',
  }
}));

const CatalogItemDetail = ({display, selectedItem}) => {
  const classes = useStyles();

  if(!selectedItem || display){
    return <div />
  }

  return (
    <div className={classes.root}>
      <Card className={classes.cardRoot}>
        <CardHeader
          title="Item detail"
          action={
            <IconButton aria-label="settings">
              <MoreVertIcon />
            </IconButton>
          }
        />
        <CardContent className={classes.cardContentRoot}>
          {selectedItem._id} <br /> <br />
          {selectedItem.partNumber} <br /> <br />
          {selectedItem.description} <br /> <br />
          {selectedItem.brand} <br /> <br />
          {selectedItem.category} <br /> <br />
        </CardContent>
      </Card>
    </div>
  )
}

export default CatalogItemDetail
